import React, {useState, useEffect} from 'react'
import './App.css';
import AddBook from './components/AddBook';
import Books from './components/Books';
import axios from 'axios'

function App() {

  const [books, setBooks] = useState([])

  const [book, setBook] = useState({
    bookName: "",
    author: "",
    quantity: "",
    section: "",
    comments: ""
  })

  useEffect(() => {
    fetch('/books').then(res => {
      if(res.ok){
        return res.json()
      }
    }).then(jsonRes => setBooks(jsonRes))
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBook(prevInput => {
      return(
        {
          ...prevInput,
          [name]: value
        }
      )
    })
  }

  const addBook = (e) => {
    e.preventDefault();
    const newBook = {
      bookName: book.bookName,
      author: book.author,
      quantity: book.quantity,
      section: book.section,
      comments: book.comments
    }
    axios.post('/newbook', newBook)
    alert(`Book ${book.bookName} added.`)
    setBook({bookName:"",author:"",quantity:"",section:"",comments:""})
  }

  const deleteBook = (id) => {
    axios.delete('/delete/' +id);
    alert(`Book with id:${id} deleted.`)
  }
  const giveBook = (id) => {
    axios.put('/give/' +id);
    alert(`Book with id:${id} gave.`)
  }
  const bringBook = (id) => {
    axios.put('/bring/' +id);
    alert(`Book with id:${id} brought.`)
  }

  return (
    <div className="App">
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">My Library</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="#">Books</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">Add Book</a>
              </li>
              <li className="nav-item dropdown">
                <a className="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                  Sections
                </a>
                <ul className="dropdown-menu">
                  <li><a className="dropdown-item" href="#">Scientific</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Sports</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Detective</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Children's</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Horror</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Tragedy</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Mystery</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Science Fiction</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Western</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Philosophy</a></li>

                </ul>
              </li>

            </ul>
            <form className="d-flex" role="search">
              <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
              <button className="btn btn-outline-success" type="submit">Search</button>
            </form>
          </div>
        </div>
      </nav>

      <Books books = {books} deleteBook = {deleteBook} giveBook = {giveBook} bringBook = {bringBook}/>
      <AddBook book = {book} handleChange = {handleChange} addBook = {addBook}/>
    </div>
  );
}

export default App;
